ENT.Type = "anim"ENT.Base = "hzn_production_ent" ENT.PrintName		= "Mining Laser"ENT.Author			= "Bynari"-- item descriptionlocal desc = {}	desc[1] = "Mines asteroids for resources but can also be used to inflict damage to other players."	desc[2] = "Requires a constant amount of energy (60 units per second)."-- production requirementslocal req = {}	req["morphite"] = 0	req["nocxium"] = 0	req["isogen"] = 0	-- factory entrylocal Entry = {}	Entry.DisplayName = ENT.PrintName	Entry.Description = desc	Entry.ClassName = "mining_laser"	Entry.BuildTime = 10	Entry.Costs = req	-- register entry with gamemodeGAMEMODE:RegisterFactoryEntry( Entry )